define('components/ArrayToString',[],function () {
	return function (input) {
		var result = '';
		if (input && input.prototype.constructor == 'Array'){
			for (var i=0; i<input.length; i++){
				result += input[i] + ' ';
			}
		}
        return result;
    };
});
require(['components/ArrayToString'], function(ArrayToString) {
    var result = ArrayToString(['word1', 'word2']);
    console.log(result);
    // <- 'word1 word2'
});
define("app", function(){});

